mapper.select:
select id, name, price, remark, date_format(createTime, '%Y-%m-%d %T') as createTime from t_goods where 1 = 1
and name like '%${name}%'
and date_format(createTime, '%Y-%m-%d') >= #{startTime}
and date_format(createTime, '%Y-%m-%d') <= #{endTime}
order by createTime desc

select t1.id, t3.name as goodsName, t3.price, date_format(t1.consumeTime, '%Y-%m-%d %T') as consumeTime, t2.name, t2.phone from t_gold_detail t1, t_wx_user t2, t_goods t3 where t1.wxId = t2.id and t1.goodsId = t3.id and t1.goldType = 0 and t1.flag = 0
and date_format(t1.consumeTime, '%Y-%m-%d') >= #{startTime}
and date_format(t1.consumeTime, '%Y-%m-%d') <= #{endTime}
order by t1.consumeTime desc
