mapper.select:

	select t2.rate
	  from t_user t1,
	       t_rule t2
	 where t1.id = #{id}
	   and t1.rule = t2.id


	select t1.id,
		   t1.consumeMoney,
		   date_format(t1.consumeTime, '%Y-%m-%d %T') as consumeTime,
	       t2.name,
	       t2.phone
	  from t_gold_detail t1,
	       t_wx_user t2
	 where t1.userId = #{id}
	   and t1.wxId = t2.id
	   and t1.flag = 0
	   
	   	 and date_format(t1.consumeTime, '%Y-%m-%d') >= #{startTime} 
	   
	   
	   	 and date_format(t1.consumeTime, '%Y-%m-%d') <= #{endTime} 
	   
	order by t1.consumeTime desc


	select t1.id,
	       t1.consumeMoney,
		   date_format(t1.confirmTime, '%Y-%m-%d %T') as confirmTime,
	       t2.name,
	       t2.phone
	  from t_gold_detail t1,
	       t_wx_user t2
	 where t1.userId = #{id}
	   and t1.wxId = t2.id
	   and t1.flag = 1
	   
	   	 and date_format(t1.confirmTime, '%Y-%m-%d') >= #{startTime} 
	   
	   
	   	 and date_format(t1.confirmTime, '%Y-%m-%d') <= #{endTime} 
	   
  order by t1.confirmTime desc


	update t_wx_user
	   set gold = gold + #{goldNum}
	 where id = #{wxUserId}


	select wxId
	  from t_gold_detail
	 where id = #{id}
