mapper.select:
SELECT u.id,u.userName,u.remark,DATE_FORMAT(u.createTime,'%Y-%m-%d %T') createTime,r.`name` roleName FROM t_user u LEFT JOIN t_user_role ur ON ur.userId = u.id LEFT JOIN t_role r on r.id=ur.roleId WHERE 1 = 1
AND u.userName like '%${userName}%'
AND ur.roleId = #{roleId}
ORDER BY createTime DESC
limit #{pageStart},#{pageSize}

SELECT count(*) FROM t_user u LEFT JOIN t_user_role ur ON ur.userId = u.id WHERE 1 = 1
AND u.userName like '%${userName}%'
AND ur.roleId = #{roleId}
